INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1459, 'Rockin'' ASP.NET MVC &amp; Javascript Developer at Clear Function (Memphis, TN) (allows remote)', '&lt;p&gt;We are looking for an experienced ASP.NET MVC developer to join us on our next project. &amp;nbsp;Here''s what you''ll be doing:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Working with a team of talented engineers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Working with expert product designers to implement compelling applications&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Creating products for interesting startup &amp;amp; established companies.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Working with a team with in-depth knowledge of modern development frameworks and agile processes.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;Please include the following with your application&lt;/p&gt;&lt;br /&gt;&lt;ol&gt;&lt;br /&gt;&lt;li&gt;An explanation of&amp;nbsp;what drives you as an engineer&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Your desired salary and/or rate&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Number of years experience with ASP.NET MVC&lt;/li&gt;&lt;br /&gt;&lt;li&gt;A link to&amp;nbsp;a GIF that makes you laugh -&amp;nbsp;http://giphy.com/&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Your most burning question about Clear Function&lt;/li&gt;&lt;br /&gt;&lt;/ol&gt;', 'Memphis, TN', 'http://careers.stackoverflow.com/jobs/99574/rockin-aspnet-mvc-javascript-developer-clear-function', 'Tue, 06 Oct 2015 05:02:02 Z', '2015-10-06T05:02:02Z', 1459);
