INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1467, 'UI/Web Designer at SemanticBits (Herndon, VA) (allows remote)', '&lt;p&gt;&lt;span&gt;SemanticBits is seeking a part-time talented UI/Web/Graphic Designer with at least four (4) years of experience in developing highly usable, interactive and modern looking web applications and websites. BS in Fine Arts or related field is required.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Designing visually elegant websites and web-based applications that adhere to usability principles&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Leading user/design specification meetings and devising UI mockups for review by team members and customers&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Collaborating with technical teams to ensure that web designs and applications meet both client and end user needs&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Developing fully documented designs of interactive web based applications that can be easily interpreted and implemented by experienced software developers&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Designing and building web 2.0 style responsive web pages and applications using HTML5, CSS3, Bootstrap, jQuery, JavaScript, Illustrator, Photoshop&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Adhering to existing design standards, including Section 508.&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Herndon, VA', 'http://careers.stackoverflow.com/jobs/99570/ui-web-designer-semanticbits', 'Tue, 06 Oct 2015 05:02:02 Z', '2015-10-06T05:02:02Z', 1467);
