INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1472, 'Automation Engineer (DevOps) - AWS at REN Cloud Solutions (Herndon, VA) (allows remote)', '&lt;p&gt;&lt;span&gt;REN Cloud Solutions is looking for a few individuals to join a talented, senior team providing best-in-class solutions for extremely complex problems and have the ability to work on the most cutting edge technology platforms and tools available. &lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;Ideally we are looking for a professional with experience in automating large scale deployments with modern configuration and deployment management systems.&lt;span&gt;&amp;nbsp;You will be working on a team of talented engineers to solve some of the most complex and exciting challenges facing the world of IT Automation and cloud deployments.&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;REN Cloud&amp;nbsp;has a number of open positions so all levels of talented candidates please apply!&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Typically this&amp;nbsp;position can pay anywhere from $70k - $150k base salary depending on skill set and experience.&amp;nbsp; Position includes countless other perks like bonus, work from home, flexibility in lifestyle, conference budget and many, many other things!&lt;/p&gt;', 'Herndon, VA', 'http://careers.stackoverflow.com/jobs/99567/automation-engineer-devops-aws-re%C4%81n-cloud-solutions', 'Tue, 06 Oct 2015 01:02:02 Z', '2015-10-06T01:02:02Z', 1472);
