INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1483, 'Full Stack Developer - Healthcare IT at Casechek (Chicago, IL) (allows remote)', '&lt;p&gt;We are looking for an experienced back-end developer who loves solving tough engineering challenges and has a passion for new technologies. You will be reporting directly to the Director of Development and working as a part of a small team. Although it is a collaborative environment, our team is self-motivated and also works independently. We strongly believe that great products can&amp;rsquo;t be created in isolation. As a part of the development team, you will interact with our healthcare customers to understand their needs and ultimately deliver the best user experience possible.&lt;/p&gt;', 'Chicago, IL', 'http://careers.stackoverflow.com/jobs/99562/full-stack-developer-healthcare-it-casechek', 'Mon, 05 Oct 2015 23:02:02 Z', '2015-10-05T23:02:02Z', 1483);
