INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1493, 'FinTech Startup - Meteor / Node.js / Javascript - Software Engineer at Ldger Inc. (New York, NY)', '&lt;p&gt;We''re seeking software engineers&amp;nbsp;to join our intimate NYC-based team to build our next generation platform for new exiting financial products.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We use - and love - Meteor as App platform, which gives us reactivity and &amp;ldquo;database everywhere&amp;rdquo; as well as a unified environment.&amp;nbsp;Our backend micro-services are built in Node.js&amp;nbsp;, integrating nicely with Meteor.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will help design and develop our financial service platform for AltFi products - allowing customers to model and execute exciting complex financial models. This includes analyzing and modeling financial data, data visualization and exporting results.&lt;/p&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/94862/fintech-startup-meteor-nodejs-javascript-ldger-inc', 'Mon, 05 Oct 2015 22:02:02 Z', '2015-10-05T22:02:02Z', 1493);
