INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1496, 'Sr. Java Developer at ADP (Pasadena, CA)', '&lt;p&gt;&lt;strong&gt;POSITION SUMMARY&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;At ADP, innovation is a core value that is now foundational to all parts of the business. Our collaborative environment is designed for collocated teams to work, live and play together focused on delivering products that solve BIG customer problems.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;With ADP building a startup culture in Pasadena, we are able to accelerate new products focused on Customer Driven Design, Data Visualization, Social, Mobile and Digital Payments. In Pasadena we are building a culture driven by "LeanStartup" principles with co-located teams, empowered to make decisions and fail fast along the way. Our goal for the Pasadena Innovation Center is to have small teams with development, design and product focused on BIG customers problems with simple elegant solutions our customers will LOVE.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;RESPONSIBILITIES:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Participate in planning, development and testing of key features&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Perform design, code, and unit test software on a predefined schedule using technologies, techniques, and tools to ensure high quality, performance, and reliability, at varying levels of complexity.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Following department coding standards, write well documented programs that perform effectively and efficiently.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Performs system analysis and design in accordance with established procedures and policies. Provides accurate estimate of time needed to complete work.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Will&amp;nbsp;develop test automation scripts with Selenium, Java, CA LISA, SOAPUI and other tools&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provides support for specification, design, testing, deployment, documentation and production.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Pasadena, CA', 'http://careers.stackoverflow.com/jobs/92408/sr-java-developer-adp', 'Mon, 05 Oct 2015 21:02:02 Z', '2015-10-05T21:02:02Z', 1496);
