INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1506, 'Front End Web Developer at Newforma (Manchester, NH)', '&lt;p&gt;Do you love web development?&amp;nbsp; Do you dream about Javascript?&amp;nbsp; Does building a modern single-page web application that is put to use by architects, engineers, and contractors around the world sound like your idea of fun?&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Newforma has a unique opportunity for a few talented developers to help us pick technology and define the architecture for our future products.&amp;nbsp; Successful candidates will work with a talented team of engineering veterans to rebuild existing applications as a modern cloud and mobile-based solution.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are a leading provider of software for design and construction firms worldwide.&amp;nbsp; Creating software that makes a difference in the Architecture, Engineering, and Construction industries is what drives our company. If you have the drive, confidence and technical expertise required, we&amp;rsquo;d like to hear from you.&lt;/p&gt;', 'Manchester, NH', 'http://careers.stackoverflow.com/jobs/94855/front-end-web-developer-newforma', 'Mon, 05 Oct 2015 21:02:02 Z', '2015-10-05T21:02:02Z', 1506);
