INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1516, 'Front end developer (ReactJS) at DrugDev Ltd (London, UK)', '&lt;p&gt;As a temporary (3 month contract) member of the Data Solutions division of DrugDev, the Front end developer will play an important role in ensuring our growing suite of applications are equipped with an outstanding user experience.&amp;nbsp; The role provides an opportunity to join a rapidly-growing team that is bringing together technology-enabled solutions to drive standards in the clinical trial industry and transforming the way that clinical trials are run.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As an excellent Front End developer, specifically with ReactJS expertise. You will:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Develop outstanding front end user experiences&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Actively collaborate with our team of back end and front end developers&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'London, UK', 'http://careers.stackoverflow.com/jobs/99551/front-end-developer-reactjs-drugdev-ltd', 'Mon, 05 Oct 2015 21:02:02 Z', '2015-10-05T21:02:02Z', 850);
