INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1524, 'Java Developer- AI Reasoning at Kasisto, Inc. (New York, NY)', '&lt;p&gt;We are looking for a talented Java Software Engineer to help design, develop and improve new and existing conversational agents and natural language integration systems.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Responsibilities:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Extending the functionality of conversational agents&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Evaluating, researching and implementing development tools and techniques&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Creating workflow designs&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Building dynamic applications with Java / J2EE&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Web Services Development using REST and SOAP&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Machine learning programming&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;a title="Watch the demo" href="https://vimeo.com/101358403"&gt;Watch the demo&lt;/a&gt;&amp;nbsp;&lt;/p&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/92393/java-developer-ai-reasoning-kasisto-inc', 'Mon, 05 Oct 2015 20:02:02 Z', '2015-10-05T20:02:02Z', 1524);
