INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1535, 'Full Stack Web Developer at Clevertech (New York, NY) (allows remote)', '&lt;p&gt;&lt;strong&gt;About You&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You have a killer background of success coding highly scalable products designed for multiple experience considerations.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You know how to ship a project and consistently iterate on new features; you value testing and documentation.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You believe that the actual language is less important than the thrill of real users finding value in your creation.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You thrive when mentoring junior developers&amp;mdash;you&amp;rsquo;re hands-on, an "expert", and always looking to learn from the people you work with.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You&amp;rsquo;re ready to immediately dive into a production environment (and even more ready for the flexibility to create your best work, again and again).&lt;/p&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/99539/full-stack-web-developer-clevertech', 'Mon, 05 Oct 2015 19:02:02 Z', '2015-10-05T19:02:02Z', 1535);
