INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1545, 'Senior Systems Administrator at TeleSign (Marina del Rey, CA)', '&lt;p&gt;We are looking to hire a Senior Systems Administrator will provide technical leadership and support to the Linux and Asterisk infrastructure, as we continue building a virtualized, distributed, voice application and processing platform using a mixture of Asterisk, Linux, Apache, Celery and&amp;nbsp; RabbitMQ backed by a world-class datacenter utilizing Cisco network hardware and load balancers.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The team environment is extremely innovative, intellectually challenging and very dynamic. You will need advanced research, troubleshooting, and people skills; the ability to work collaboratively; strong analytical skills; a great work ethic; and a drive to succeed.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Job Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design, implement and maintain the server farms and voice/messaging infrastructure.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Manage the redundancy and failover systems.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide regular status updates on projects and tickets, including progress updates and identification of technical risks and issues associated with them.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide technical guidance and training to other staff members within the organization.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Tune and monitor application and operating system performance.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Maintain the security and integrity of the network and systems.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Support production systems, analyze problems, and configure systems within the distributed computing environment.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Marina del Rey, CA', 'http://careers.stackoverflow.com/jobs/95343/senior-systems-administrator-telesign', 'Mon, 05 Oct 2015 19:02:02 Z', '2015-10-05T19:02:02Z', 1);
