INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1546, 'Software Engineer - iOS at TeleSign (Marina del Rey, CA)', '&lt;p&gt;Are you an experienced iOS developer who is passionate about security and authentication? Do you have hands-on experience with iOS applications and SDKs? If so, then TeleSign is looking for you! We&amp;rsquo;re looking for a strong engineer to join our team to build client applications and SDKs in iOS.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work on TeleSign&amp;rsquo;s iOS authentication apps&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Produce well designed and well tested production quality code.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Accountable for complete development process; design, development, testing, and deployment.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Participate in design and code reviews to help increase the quality of our products.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Pick best technology for situation.&amp;nbsp; The ability to tell the difference between cool and useful.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ensure best practices for building secure software are followed.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Interact with Product Owner effectively regarding features and specifications.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Marina del Rey, CA', 'http://careers.stackoverflow.com/jobs/98789/software-engineer-ios-telesign', 'Mon, 05 Oct 2015 19:02:02 Z', '2015-10-05T19:02:02Z', 1);
