INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1553, 'Senior Software Engineer at Financial Engines (Boston, MA)', '&lt;p&gt;Come join our platform development team, responsible for building and customizing products and collaborating with external partners in the financial services field. We are looking for someone with strong communication and client facing skills. This person would have a blend of front and backend technology, with a preference for up to date front end skills and experience working with or for financial services companies.&lt;/p&gt;', 'Boston, MA', 'http://careers.stackoverflow.com/jobs/99536/senior-software-engineer-financial-engines', 'Mon, 05 Oct 2015 18:02:02 Z', '2015-10-05T18:02:02Z', 1553);
