INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1560, 'Ruby on Rails Developer at a Fast-Growing Healthcare IT Company at CoverMyMeds (Columbus, OH) (allows remote)', '&lt;p&gt;We&amp;rsquo;re looking for experienced developers to join our team. Need to learn Ruby? No problem. Training&amp;rsquo;s on us. Once you&amp;rsquo;re on board, you&amp;rsquo;ll jump into our three-week Ruby on Rails training program.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a Developer at CoverMyMeds, you create solutions that make our healthcare system work more efficiently and effectively in order to help millions of patients receive their prescription medications every year.&amp;nbsp; You build products that our partners in healthcare use everyday, and innovate products that don''t even exist yet.&amp;nbsp; You work on small teams with other really smart developers with endless opportunities to take on responsibility, continue learning and mentor others.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Our Tech Stack:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Ruby on Rails, Python, PHP, Backbone.js, SQL-Server, PostgreSQL, Git, Elasticsearch, Capistrano, Redis, Sidekiq, Vagrant, Puppet, Apache, RSpec, Capybara, Cucumber, Watir&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&amp;nbsp;What You''ll Do:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design and develop web applications and APIs for&amp;nbsp;the CoverMyMeds product line&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design and develop internal support software&amp;nbsp; for our own awesome coworkers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Help our world-class support team solve tough customer issues&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Contribute to and improve revision control and deployment in our fast-paced, agile environment&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Columbus, OH', 'http://careers.stackoverflow.com/jobs/76888/ruby-on-rails-developer-at-a-fast-growing-covermymeds', 'Mon, 05 Oct 2015 18:02:02 Z', '2015-10-05T18:02:02Z', 1560);
