INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1562, 'Principal Front End Developer at Deep Intel (Seattle, WA)', '&lt;p&gt;In a technological world where change is the only constant, it takes vision, experience and a wide range of capabilities to help businesses overcome their technology challenges.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;From design to development to support to management, we&amp;nbsp;offer decades of experience, proven frameworks and a disciplined approach to quickly deliver reliable, quality solutions that help you go to market faster and get a leg up on your competition. As a Microsoft National Systems Integrator, in addition to working on projects close to home, you also have the ability to work with cross-functional/regional teams &amp;ndash; giving you flexibility to determine a work/life balance that&amp;rsquo;s best suited for you.&lt;/p&gt;', 'Seattle, WA', 'http://careers.stackoverflow.com/jobs/98442/principal-front-end-developer-deep-intel', 'Mon, 05 Oct 2015 18:02:02 Z', '2015-10-05T18:02:02Z', 1562);
