INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1568, 'Sr. Frontend Engineer at Playtika Santa Monica (Santa Monica, CA)', '&lt;p&gt;&lt;strong&gt;About us:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Our mission is to create fun and accessible social games that introduce casual audiences to new experiences that go above and beyond the current offerings.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Headquartered in Santa Monica, California, &lt;strong&gt;our team powers the #1 Bingo game on Facebook, BINGO Blitz! &lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are looking for talented people who want to make a big impact on the games we make. Our culture is relaxed, creative and fun, with excellent compensation and benefits packages.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What we are looking for:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Playtika is seeking a Senior Frontend Engineer to work with our team to develop the next generation of social games in our Santa Monica, CA offices.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work with the engineering team to develop social games on Facebook, iOS and Android.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Find smart solution together with the creative team to deliver top performance on every platform.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Maintain, profile and debug applications that will be used by millions of users every day.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Santa Monica, CA', 'http://careers.stackoverflow.com/jobs/97938/sr-frontend-engineer-playtika-santa-monica', 'Mon, 05 Oct 2015 17:02:02 Z', '2015-10-05T17:02:02Z', 760);
