INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1570, 'Software Tester (m/w) at SCHUFA Holding AG (Wiesbaden, Deutschland)', '&lt;p&gt;&lt;strong&gt;Ihre Aufgaben:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Durchf&amp;uuml;hrung, Auswertung und Dokumentation von automatisierten Testf&amp;auml;llen f&amp;uuml;r funktionale und nicht-funktionale Anforderungen&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Programmierung der Testskripte mit Hilfe geeigneter Automatisierungswerkzeuge sowie die Generierung von Testdaten (z.B. mittels SQL-Scripten)&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Bewertung und Kommunikation von Testergebnissen, der aktuellen Testabdeckung und des Testfortschritts&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Unterst&amp;uuml;tzung bei Aufbau Konfiguration und Betrieb von Testumgebungen&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Fortlaufende Optimierung der Testumgebungen und Testverfahren&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Wiesbaden, Deutschland', 'http://careers.stackoverflow.com/jobs/96464/software-tester-m-w-schufa-holding-ag', 'Mon, 05 Oct 2015 17:02:02 Z', '2015-10-05T17:02:02Z', 1570);
