INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1574, 'Front-End / Web Developer at Affectv (London, UK)', '&lt;ul&gt;&lt;br /&gt;&lt;li&gt;You will work on an amazing variety of products: data management interfaces, personalised online advertising, and SAAS platforms.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You will join our SignalAds team working on our state-of-the-art real-time personalised advertising technology, so you will be able to contribute your ideas on how we build our systems, and which technologies and methodologies we use.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You will focus on front-end development. You will work with other talented developers on building interfaces that control and report on our Big-Data Technologies.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Research, test and implement new technologies, software and services.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'London, UK', 'http://careers.stackoverflow.com/jobs/99527/front-end-web-developer-affectv', 'Mon, 05 Oct 2015 17:02:02 Z', '2015-10-05T17:02:02Z', 1574);
