INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1576, 'Lead Software Engineer at Cvent (McLean, VA)', '&lt;p&gt;&lt;strong&gt;Lead Software Engineer&lt;/strong&gt;&lt;br&gt;&amp;nbsp;&lt;br&gt;&lt;br&gt;&lt;strong&gt;Position Description:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are looking for Lead Software Engineers to join our rapidly growing Technology Team. Cvent&amp;rsquo;s Technology Team is responsible for the design, development, and operation of the company&amp;rsquo;s suite of cloud-based software products. We are currently engaged in a major initiative to build our next generation software platform, utilizing the most cutting edge tools and technologies.&lt;br&gt;&lt;br&gt;&lt;strong&gt;What You Will Be Doing&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work on large-scale Internet applications, where performance, reliability and scalability, are critical design goals &amp;ndash; not after-thoughts.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Create beautiful, interactive and easy-to-use web applications using rich client-side code and MVC based server-side code.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Create innovative mobile applications on a wide range of platforms including iOS, Android and others.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Contribute to our core Service-Oriented Architecture (SOA) and Event-Driven Architecture (EDA) in real-life applications.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work with large modern distributed systems and polyglot persistence technologies including RDBMS, RabbitMQ, CouchBase, Cassandra, and Elasticsearch.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Lead and mentor teams of dedicated engineers to build powerful products in an iterative Agile or Kanban environment.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Need more reasons to consider us? &lt;/strong&gt;We offer competitive salary packages, an extensive benefits package for all full-time employees, including medical, dental, vision, stock options, bonuses, 401K, gym membership discounts and other great perks! If you would like to learn more about Cvent and our products, visit our website at www.cvent.com.&lt;/p&gt;', 'McLean, VA', 'http://careers.stackoverflow.com/jobs/99024/lead-software-engineer-cvent', 'Mon, 05 Oct 2015 17:02:02 Z', '2015-10-05T17:02:02Z', 1576);
