INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1581, 'Senior Software Engineer (Front-End) at Financial Engines (Sunnyvale, CA)', '&lt;p&gt;Financial Engines, a fast-growing public company with a mission to solve the retirement puzzle for millions of Americans is seeking a lead engineer to develop web applications using modern technology. &amp;nbsp;As a lead engineer of one of the agile teams that''s at the forefront of developing world class customer experience, you&amp;rsquo;ll provide technical leadership in developing responsive/elegant UI applications using modern UI framework such as Angular JS, Bootstrap, etc. powered by REST APIs.&lt;/p&gt;', 'Sunnyvale, CA', 'http://careers.stackoverflow.com/jobs/99525/senior-software-engineer-front-end-financial-engines', 'Mon, 05 Oct 2015 17:02:02 Z', '2015-10-05T17:02:02Z', 1553);
