INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1590, 'Senior Java Software Developer at O''Reilly Auto Parts (Springfield, MO) (allows remote)', '&lt;p&gt;&lt;span&gt;O&amp;rsquo;Reilly Auto Parts is a rapidly growing fortune 500 company where our growth is supported by technology! We are looking for motivated techies who have passion and want to make an impact!&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are looking for a motivated Java Developer with strong analytical skills to work in a fast paced environment.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;A successful candidates will be self-starters that enjoy working with a team of other techies. This candidate needs to have multiple years experience in web development with an eye for detail and an appetite to leverage technology to solve business challenges. Must have experience in Java, SQL, jQuery, Twitter-Bootstrap, HTML, CSS.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The person filling this position must be a hands-on, proactive, self-motivated, go getter, with a strong desire to succeed in a challenging environment.&amp;nbsp; We value enthusiasm, hard work and dedication to our company and our way of doing business.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;If you have passion for Java development and take pride in your work, then O&amp;rsquo;Reilly is looking for you. Be ready to push your boundaries and take your skills to the next level. The opportunity to work remote is based on experience.&amp;nbsp;&lt;/p&gt;', 'Springfield, MO', 'http://careers.stackoverflow.com/jobs/82468/senior-java-software-developer-oreilly-auto-parts', 'Mon, 05 Oct 2015 16:02:02 Z', '2015-10-05T16:02:02Z', 1590);
