INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1610, 'Back-end Developer (Golang) at Ecosia (Berlin, Germany)', '&lt;p&gt;Ecosia is the search engine that plants trees. As part of our mission to cultivate a more environmentally, socially and economically sustainable world, our goal is to reverse the tide of deforestation by planting a billion native trees by 2020.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We''re also not a typical company &amp;ndash; a social business and one of the first B Corporations in Europe &amp;ndash; and this is by no means a typical nine-to-five job. With 2 million users, 25 million searches and more than 1,000 user evangelists, we are now looking for a passionate Back-end Developer who will be responsible for keeping our products on the lean cutting-edge.&lt;/p&gt;', 'Berlin, Germany', 'http://careers.stackoverflow.com/jobs/99523/back-end-developer-golang-ecosia', 'Mon, 05 Oct 2015 16:02:02 Z', '2015-10-05T16:02:02Z', 1610);
