INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1617, 'Full Stack Java Developer at Nucleus Financial (Edinburgh, UK)', '&lt;p&gt;As a software developer&amp;nbsp;at Nucleus, you will help design, develop, and implement customer facing web applications for an award winning Wrap provider.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will be working on the full stack, and will have the ability to influence it. The development team is a relatively new addition to Nucleus and while there is a strong foundation in place, there are plenty of opportunities to make a real difference as we grow.&amp;nbsp; We&amp;rsquo;re looking for passionate people who care about what they do and how they do it. You will be expected to continue our tradition of surprising and delighting our users by giving them the tools that make a difference to their business.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We&amp;rsquo;re looking for people who believe in showing, not telling. People who look to test and demonstrate their ideas before discussing both the pros and cons of the solution.&lt;/p&gt;', 'Edinburgh, UK', 'http://careers.stackoverflow.com/jobs/93274/full-stack-java-developer-nucleus-financial', 'Mon, 05 Oct 2015 15:02:02 Z', '2015-10-05T15:02:02Z', 1617);
