INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1625, 'Full Stack Developer at Pagefair (Dublin, Ireland)', '&lt;p&gt;&lt;strong&gt;&lt;strong&gt;WHO WE ARE&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;PageFair helps websites survive the rise of adblocking. PageFair gives websites the ability to measure how many visitors block their ads, and unlock revenue with non-intrusive ads designed to be acceptable to adblock users. We are solving a problem that websites deeply care about, and which directly affects the sustainability of online content creation.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Right now, we&amp;rsquo;re looking for some great developers to join our team in Dublin 6. If you''re a highly-talented technologist who cares about using your skills to solve meaningful problems, we want to hear from you!&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;strong&gt;WHAT WE''RE LOOKING FOR&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Someone who is:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Familiar with Linux and prefers developing on LAMP-like stacks.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Concerned with being able to communicate ideas clearly.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Proud of your craft: you care about details and always try to do things the right way.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Someone who has:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Full-stack experience, from CSS through to web frameworks and relational databases.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;A deep interest in development.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Broad interests in additional disciplines, such as design, communication, and business.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Bonus points if you have any of the following:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Contributions to open source projects or any independent project work.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Strong Javascript, Node.js or Python abilities.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience scaling web applications and working with AWS.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;We want to surround ourselves with amazing people who are fun to work with. If you want the same and have the experience we''re looking for, get in touch and apply direct!&lt;/p&gt;', 'Dublin, Ireland', 'http://careers.stackoverflow.com/jobs/99520/full-stack-developer-pagefair', 'Mon, 05 Oct 2015 15:02:02 Z', '2015-10-05T15:02:02Z', 1625);
