INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1627, 'SAP Netweaver Technical Consultant at KPN (Maastricht-Airport, Netherlands)', '&lt;p&gt;&lt;strong&gt;&lt;em&gt;SAP Netweaver Technical Consultant&lt;/em&gt;&lt;/strong&gt;&lt;em&gt;&lt;br&gt;Jouw rol als SAP Netweaver Technical Consultant&lt;/em&gt;&lt;br&gt;&lt;br&gt;Als ervaren SAP Netweaver Technical Consultant ben je verantwoordelijk voor:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Verrichten van vooronderzoek naar wensen opdrachtgever (haalbaarheid) en het vertalen van (klant)problemen naar technische oplossingen o.b.v. SAP Netweaver en SAP HANA software.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Het leveren van technische ondersteuning en nemen van verantwoordelijkheid bij het opleveren van SAP systemen, het in beheer nemen van vernieuwde componenten en bij voorkomende calamiteiten.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Beheren van complexe klant infrastructuren: changes, aanbevelingen, verstoringen, etc.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Het deelnemen aan technische SAP projecten (installaties, upgrades, migraties).&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Begeleiding en coacing van, bij ontwerp- en realisatie betrokken, medewerkers.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Uitwerken van adviezen in een systeemvoorstel inclusief kostenonderbouwing.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Optreden als deelprojectleider/ co&amp;ouml;rdinator in test-/ implementatiefase.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Maastricht-Airport, Netherlands', 'http://careers.stackoverflow.com/jobs/99519/sap-netweaver-technical-consultant-kpn', 'Mon, 05 Oct 2015 15:02:02 Z', '2015-10-05T15:02:02Z', 1627);
