INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1638, 'Help improve the NHS - Developer ASP.NET, MVC, C#, jQuery at SSentif Ltd (Leeds, UK)', '&lt;p&gt;SSentif are developing a dedicated reporting solution using the full spectrum of modern .net technologies to supplement our current reports functionality.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;An opportunity has arisen for a developer to join our team to implement and grow the solution.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The new solution will be developed using .NET 4.5, MVC 5, Bootstrap, CSS 3, Chart.js, jQuery 2.1, SQL Server 2014 and IIS 7 on top of an OLAP&amp;nbsp;data source.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;SSentif offers great scope for career progression and have an enviable reputations for developing talented individuals. With zero attrition rate amongst our employees this is a fantastic and rare opportunity to join this high growth/high tech company.&lt;/p&gt;', 'Leeds, UK', 'http://careers.stackoverflow.com/jobs/99515/help-improve-the-nhs-developer-aspnet-mvc-c-sharp-ssentif-ltd', 'Mon, 05 Oct 2015 14:02:02 Z', '2015-10-05T14:02:02Z', 1638);
