INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1640, 'Entwickler/in C++ und C# fr Druckbildinspektionssysteme at EyeC GmbH (Hamburg, Deutschland)', '&lt;p&gt;&lt;strong&gt;Entwickler/in f&amp;uuml;r C++ und C# &lt;/strong&gt;&lt;strong&gt;in Vollzeit&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br&gt;Als Technologief&amp;uuml;hrer im Bereich der Druckbildinspektionssysteme m&amp;uuml;ssen wir st&amp;auml;ndig innovative L&amp;ouml;sungen entwickeln. Diese L&amp;ouml;sungen in ausgereifte Produkte zu verwandeln ist eine anspruchsvolle Aufgabe, die eine hohe fachliche Kompetenz und das Verst&amp;auml;ndnis f&amp;uuml;r Kundenanforderungen voraussetzt. Daher suchen wir Sie und Ihre Erfahrung f&amp;uuml;r die Weiterentwicklung und Pflege unserer Produkte.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Ihre Aufgaben:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Sie sind f&amp;uuml;r die Weiterentwicklung und die Wartung unserer Software zust&amp;auml;ndig.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Sie integrieren neue Softwaremodule in die bestehende Systemarchitektur und vervollst&amp;auml;ndigen eigenverantwortlich die Spezifikation.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Sie unterst&amp;uuml;tzen uns mit Ihrer Erfahrung dabei, unseren DevOps-Prozess zu verbessern.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Sie unterst&amp;uuml;tzen die Applikationsingenieure bei der Inbetriebnahme neuer Produkte.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Hamburg, Deutschland', 'http://careers.stackoverflow.com/jobs/94792/entwickler-in-c-plus-plus-und-c-sharp-f%C3%BCr-eyec-gmbh', 'Mon, 05 Oct 2015 14:02:02 Z', '2015-10-05T14:02:02Z', 1640);
