INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1646, 'DevOps Engineer at PowerReviews (San Francisco, CA)', '&lt;p&gt;As a DevOps Engineer, you will deliver infrastructure to support our growing technology, build automated frameworks for code delivery, design our test and production environments, plan for growth, analyze and fix problems in real time, and design and implement solid networks.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Maintain and support PowerReviews production applications, users and clients.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Guide software development teams, help them design scalable systems with efficient and cost effective use of hardware.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design and launch CloudFormation stacks in AWS, relying on puppet, ruby, and linux fundamentals.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Maintain and improve the build, test and release frameworks for various technologies.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Execute software releases to production, QA and development environments&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Create new tools and scripts as needed to support our business, relying mostly on ruby, shell and perl.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide leadership and practical knowledge of all aspects of Amazon Web Services.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Write well documented and tested code intended for automated execution.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Participate in and contribute to peer code reviews.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ensure reliability of a broad array of networks and systems, including on-call rotation and second-tier support.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Create and implement log storage, monitoring, alerting and metrics gathering.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Participate in 24h on-call rotation supporting and reacting to production issues.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Understand and uphold Information Security requirements throughout the PowerReviews systems.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'San Francisco, CA', 'http://careers.stackoverflow.com/jobs/98296/devops-engineer-powerreviews', 'Mon, 05 Oct 2015 14:02:02 Z', '2015-10-05T14:02:02Z', 1646);
