INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1655, 'Extraordinary Senior Java Developer (Real-time big data) at Nordea, Capital Markets IT (Copenhagen, Denmark)', '&lt;p&gt;We are building Nordea Capital Markets'' next generation Middle Office IT solutions: Liquidity management, accounting, statutory reporting and controlling frameworks. We are looking for a Senior Java Developer to actively contribute to the design, development and rollout of new solutions. This includes working accurately with large amounts of data in a timely manner, sometimes in real-time.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will be part of an informal and strong development team building services and systems using Java and industry standard tools and methodologies. We use agile processes in small teams in close corporation with our business owners.&lt;/p&gt;', 'Copenhagen, Denmark', 'http://careers.stackoverflow.com/jobs/89173/extraordinary-senior-java-developer-real-time-nordea-capital-markets-it', 'Mon, 05 Oct 2015 14:02:02 Z', '2015-10-05T14:02:02Z', 1655);
