INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1658, 'Vodafone zoekt technische professionals met een visie op de zakelijke markt at Vodafone (Eindhoven, Netherlands)', '&lt;p&gt;&lt;em&gt;De technische problemen van vandaag of de slimme oplossingen voor morgen?&lt;br&gt;&lt;strong&gt;Vodafone zoekt technische professionals met een visie op de zakelijke markt&lt;/strong&gt;&lt;br&gt;&lt;/em&gt;&lt;br&gt;Onze klanten willen 24/7 toegang hebben tot de wereld om hen heen. Daarom zijn we wereldwijd de grootste investeerder in ons netwerk, en heeft onafhankelijk onderzoek bevestigd dat Vodafone het beste mobiele netwerk van Nederland heeft. &lt;br&gt;&lt;br&gt;Naast het ontwerp, de ontwikkeling, uitrol en beheer van ons vaste en mobiele netwerk ontwikkelen onze technology professionals echter ook innovatieve oplossingen voor de consumenten- en zakelijke markt. Vari&amp;euml;rend van interfaces voor onze TV box tot de volledige IT-infrastructuur voor kantoren. Onze belangrijkste uitdaging de komende tijd is onze klanten een totaaloplossing te bieden op het gebied van telefonie, internet en televisie. Kom jij daaraan bijdragen?&lt;/p&gt;', 'Eindhoven, Netherlands', 'http://careers.stackoverflow.com/jobs/99513/vodafone-zoekt-technische-professionals-met-een-vodafone', 'Mon, 05 Oct 2015 14:02:02 Z', '2015-10-05T14:02:02Z', 1658);
