INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1660, 'Senior Software Engineer (Java) at AKQA (London, UK)', '&lt;p&gt;AKQA Senior Software Engineers are inspired by technology and have a passion for creating innovative technical solutions.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will love working in a team of highly skilled people, all with strong opinions about various technologies, frameworks and approaches to delivery. You can articulate your ideas to your colleagues effectively and will take on board the ideas of others. You thrive when faced with new challenges and will constantly look to improve client deliverables.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a Senior Software Engineer, you will build innovative websites, content management systems and services using web and industry best practices. You will create robust solutions that are easy to deploy and a delight to operate. You will trail blaze new and innovative solutions using the latest server-side technologies.&lt;/p&gt;', 'London, UK', 'http://careers.stackoverflow.com/jobs/99511/senior-software-engineer-java-akqa', 'Mon, 05 Oct 2015 14:02:02 Z', '2015-10-05T14:02:02Z', 1660);
