INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1662, 'Softwareentwickler (m/w) at Brandstock Services AG (Mnchen, Deutschland)', 'Die Brandstock Gruppe ist ein Anbieter von innovativen Dienstleistungen im Bereich des gewerblichen Rechtsschutzes. Blue-Chip-Firmen, kleine und mittelst&amp;auml;ndische Unternehmen sowie Anwaltskanzleien sch&amp;auml;tzen unseren effizienten Rundum-Service. Wir &amp;uuml;berzeugen durch kundenindividuelle Beratung und ma&amp;szlig;geschneiderte L&amp;ouml;sungen. Die &lt;strong&gt;Brandstock Services AG&lt;/strong&gt; unterst&amp;uuml;tzt ihre Kunden&amp;nbsp; u.a. bei weltweiten Markenrecherchen, Markenverl&amp;auml;ngerungen und der Koordination gro&amp;szlig;er Marken- und Patentumschreibungsprojekte.&lt;br /&gt;&lt;p&gt;Unsere Philosophie liegt in der F&amp;ouml;rderung und Wertsch&amp;auml;tzung eines jeden Mitarbeiters, um mit dessen Ideen mit zeitgem&amp;auml;&amp;szlig;en L&amp;ouml;sungen und neuen Ans&amp;auml;tzen gemeinsam zu wachsen.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Wir haben noch viel vor &amp;ndash; und h&amp;auml;tten Sie gerne mit an Bord als&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Softwareentwickler (m/w)&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Ihre Aufgaben&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Erstellen von Web-Anwendungen auf Basis von ASP.NET, C#&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Entwickeln von Windows-Anwendungsprogrammen mit Windows Forms&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Office-Development (VBA, VSTO)&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Datenbankdesign (MSSQL) &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;Ihr Profil&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Studium im Bereich Informatik bzw. Ausbildung zum Fachinformatiker (Anwendungsentwicklung) oder vergleichbare Qualifikation&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Sehr gute Kenntnisse in HTML und JavaScript sowie in der .NET-Technologie&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Programmierkenntnisse in Windows-Anwendungsentwicklung und MS SQL Server&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Gute Kenntnisse mit Microsoft Visual Studio und Team Foundation Server sowie Kenntnisse in WCF und WWF&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Erfahrung im Umgang mit Telerik UI Controls und Aspose.Cells sind von Vorteil&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Sehr gute Englischkenntnisse in Wort und Schrift&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;systematische, detailgetreue und zielgerichtete Arbeitsweise gepaart mit analytischen und konzeptionellen F&amp;auml;higkeiten&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;aufgeschlossene Pers&amp;ouml;nlichkeit und Teamgeist&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;Wir bieten&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Eine interessante, eigenverantwortliche Arbeit in einem internationalen Team, das sich mit viel Engagement und Leidenschaft f&amp;uuml;r unsere namenhaften Kunden einsetzt&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Verkehrsg&amp;uuml;nstiger Standort im Herzen von M&amp;uuml;nchen&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Markt- und leistungsgerechte Verg&amp;uuml;tung, flexible Arbeitszeiten, Altersvorsorge, Mitarbeiterbonusprogramme&amp;hellip; und vieles mehr&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;Wir freuen uns auf Ihre ausf&amp;uuml;hrlichen Bewerbungsunterlagen inkl. Verf&amp;uuml;gbarkeit und Gehaltsvorstellung &amp;uuml;ber unser Bewerbungsportal.&lt;/p&gt;', 'Mnchen, Deutschland', 'http://careers.stackoverflow.com/jobs/99510/softwareentwickler-m-w-brandstock-services-ag', 'Mon, 05 Oct 2015 13:02:02 Z', '2015-10-05T13:02:02Z', 1662);
