INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1666, 'Business Intelligence Analyst/Developer at Merchants Bonding Company (West Des Moines, IA)', '&lt;p&gt;&lt;strong&gt;Our Future&amp;rsquo;s So Bright, We&amp;rsquo;ve Gotta Wear Shades!&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Known for online innovation, Merchants Bonding Company&amp;rsquo;s future growth plans will necessitate the need for a Business Intelligence Analyst/Developer.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Working with other team members and business stakeholders, you&amp;rsquo;ll create an effective BI and reporting platform that can be shared across product lines and businesses.&lt;/p&gt;', 'West Des Moines, IA', 'http://careers.stackoverflow.com/jobs/99509/business-intelligence-analyst-developer-merchants-bonding-company', 'Mon, 05 Oct 2015 13:02:02 Z', '2015-10-05T13:02:02Z', 1666);
