INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1673, 'PHP Entwickler m/w at xTWO GmbH (Hungen, Deutschland)', '&lt;p&gt;&lt;strong&gt;Wir sind ein Tochterunternehmen der RIB Software AG (TecDax) und expandieren stark im&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; E-&amp;shy; Commerce im Bereich Sanit&amp;auml;r/Bad/Heizung.&lt;br&gt;F&amp;uuml;r den nationalen und internationalen Ausbau unserer Internet-&amp;shy;Plattformen&lt;br&gt;suchen wir eine/n Mitarbeiter/in als&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;br&gt;PHP Entwickler/in&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Arbeitsaufgaben:&lt;/strong&gt;&lt;br&gt;&amp;bull; Weiterentwicklung und Optimierung bestehender Anwendungen&lt;br&gt;&amp;bull; Konzeption komplexer Abl&amp;auml;ufe und Funktionsweisen&lt;br&gt;&amp;bull; Entwicklung neuer Anwendungen mit Fokus auf Datenschutz, Ausfallsicherheit und Performance&lt;br&gt;&amp;bull; Anbindung externer Dienste &amp;uuml;ber Schnittstellen&lt;br&gt;&amp;bull; Dokumentation und Wartung von Anwendungen&lt;/p&gt;', 'Hungen, Deutschland', 'http://careers.stackoverflow.com/jobs/99483/php-entwickler-m-w-xtwo-gmbh', 'Mon, 05 Oct 2015 13:02:02 Z', '2015-10-05T13:02:02Z', 1673);
