INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1677, 'PHP / MySQL developer needed by leader in Online Customer Service at Synthetix Ltd (Takeley, UK)', '&lt;p&gt;&lt;strong&gt;Would you like the opportunity to help us build our next generation of integrated customer service applications? If you have at least two years'' experience in PHP with MySQL and are familiar with RESTful APIs, &lt;em&gt;we want to hear from you!&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We have an immediate vacancy for a talented PHP programmer, who will be based out of our lovely new barn-style offices near Stansted in Essex.&amp;nbsp; If you have PHP and MySQL skills, have built database-backed websites and are used to working with APIs to integrate with services such as Twitter/Facebook or Salesforce, this is the job for you.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The successful candidate will be compensated with an attractive salary package, commensurate with skills and experience.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Working for Synthetix&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Read our Company page for some existing employee opinions on what it''s like to work at Synthetix!&amp;nbsp; We try and create a fun, informal atmosphere for people to work in.&amp;nbsp; There''s a lot of camaraderie and a lot of laughs.&amp;nbsp; Many of us choose to socialise together outside of working hours and the company organises several annual get-togethers to encourage this - most recently the entire company spent three days at the Edinburgh festival.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Management&lt;/strong&gt;&lt;br&gt;&lt;br&gt;Synthetix runs a flat management structure which emphasises individual responsibility and task ownership.&amp;nbsp; Every employee is greatly valued and listened to, resulting in an environment with great communication between the development, sales and marketing departments.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;em&gt;Simply put, we listen to your ideas and we let you get on with it.&lt;/em&gt;&lt;br&gt;&lt;br&gt;&lt;strong&gt;Package&lt;/strong&gt;&lt;br&gt;&lt;br&gt;As well as a competitive salary and holiday entitlement, Synthetix is happy to offer childcare voucher schemes and pension contributions via tax efficient salary sacrifice methods.&amp;nbsp; We will offer a workplace pension to all employees in Autumn of 2016.&lt;br&gt;&lt;br&gt;&lt;strong&gt;As an ethical employer, Synthetix follows the principles laid down by EHRC and ACAS.&amp;nbsp;&lt;/strong&gt; &lt;strong&gt;Please note that if you are applying from outside of the European Union, we cannot sponsor your visa application.&lt;/strong&gt;&lt;/p&gt;', 'Takeley, UK', 'http://careers.stackoverflow.com/jobs/99505/php-mysql-developer-needed-by-leader-in-online-synthetix-ltd', 'Mon, 05 Oct 2015 12:02:02 Z', '2015-10-05T12:02:02Z', 1677);
