INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1682, 'Senior Software Engineer - Backend at Bitbar (Helsinki, Finland)', '&lt;p&gt;We at Bitbar are looking for a Backend Software Engineer to design, build, scale and continuously improve the software stack serving the leading mobile companies in the world.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We hope You have experience in cutting-edge technologies to create high-performance services that really scale.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will have the freedom to design our new microservices platform from ground up.&lt;br&gt;&lt;br&gt;&lt;strong&gt;Job Description&lt;/strong&gt;&lt;br&gt;&lt;br&gt;- Design and build our totally new distributed microservices landscape and make it scale in global context&lt;br&gt;- Write elegant and easily readable code&lt;br&gt;- Use agile methodologies&lt;br&gt;- Think big-picture: Continuously evaluate and challenge our architecture and processes&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are a young team and work together hand in hand in a start-up-atmosphere. We appreciate flat hierarchies and give anyone of us time and space to develop him/herself personally and professionally. Our working times are flexible and our motivation is big.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The position is in Helsinki, Finland&lt;/p&gt;', 'Helsinki, Finland', 'http://careers.stackoverflow.com/jobs/99494/senior-software-engineer-backend-bitbar', 'Mon, 05 Oct 2015 11:02:02 Z', '2015-10-05T11:02:02Z', 1682);
