INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1684, 'Software Engineer, Performance at Suitable Technologies, Inc. (Palo Alto, CA)', '&lt;p&gt;&lt;strong&gt;Job Description&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;We''re looking for senior level engineers to develop innovative, robust, and solid software for many aspects of our smart presence products.&amp;nbsp;We are seeking a seasoned systems performance software engineer with the passion and experience necessary to design, develop, and support Beam technology.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;Roles and Responsibilities&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design, build, and deploy our core web service infrastructure&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Main work will be in the design and implementation of overhead, scalability, stability, and stress tests using industry standard tools and custom developed software written in Python and Django.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ownership of significant areas of the code for both new feature/functionality development as well as to maintain/redesign of the existing areas of the product&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop new features, and test coverage,&amp;nbsp;based on product needs and opportunities for technical innovation&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide accurate scoping, clear strategies, and milestones for performance tests&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide expertise for use in patches, research projects, sizing, capacity planning, and hardware and network configuration&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Palo Alto, CA', 'http://careers.stackoverflow.com/jobs/99492/software-engineer-performance-suitable-technologies-inc', 'Mon, 05 Oct 2015 10:02:02 Z', '2015-10-05T10:02:02Z', 1684);
