INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1688, 'Solutions Architect / Backend Lead Developer at Sticky (Stockholm, Sweden)', '&lt;p&gt;Sticky is a fast growing, international, startup. We have just launched our first product in beta and are seeing great traction. To our Stockholm office we are hiring a Solutions Architect / Backend Lead Developer.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br&gt;&lt;strong&gt;What a Solutions Architect at Sticky would do&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Be responsible for our scalable cloud architecture on AWS (EC2, S3, SQS)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop and deploy high-performing back-end services using Python/Node.js&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop the architectural vision&amp;nbsp;and be proactive about scaling and performance&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Inspire and lead other backend developers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Collaborate with a variety of international colleagues in a truly global environment&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Stockholm, Sweden', 'http://careers.stackoverflow.com/jobs/99491/solutions-architect-backend-lead-developer-sticky', 'Mon, 05 Oct 2015 10:02:02 Z', '2015-10-05T10:02:02Z', 1688);
