INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1693, 'Projektleiter Automotive Software (m/w) at Harman Becker Automotive Systems GmbH (Hildesheim, Deutschland)', '&lt;p&gt;&lt;strong&gt;Ihre Aufgaben:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Entwicklung und Architektur von objektorientierter Embedded-Software f&amp;uuml;r Fahrerassistenz- und Komfortfunktionen in Kundenprojekten&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Anforderungsanalyse, Erstellen des Designs, Implementierung, Dokumentation sowie Fehleranalyse und Fehlerbehebung&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Inbetriebnahme und Verifikation der erstellten Software basierend auf Simulationen sowie mit realen Steuerger&amp;auml;ten und in Erprobungsfahrzeugen&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Wartung und Pflege bestehender Softwaremodule und Anpassung an verschiedene Fahrzeugtopologien&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Erarbeitung von Detailspezifikationen und technische Abstimmungen mit Kunden sowie mit internen und externen Partnern&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Durchf&amp;uuml;hrung von System-/Performanceanalysen und -optimierungen im verantworteten Bereich&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Erstellung von Aufwands- und Risikoabsch&amp;auml;tzungen f&amp;uuml;r zu entwickelnde SW-Module&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Unser Angebot:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Setzen Sie Ihre Karriere bei HARMAN, einem weltweit f&amp;uuml;hrenden Unternehmen in den Bereichen Infotainment und Car Audio fort. Wir sind seit vielen Jahren erfolgreich am Markt aktiv und z&amp;auml;hlen die gr&amp;ouml;&amp;szlig;ten Premium-Automobilhersteller zu unseren Kunden. Als Mitarbeiter bei HARMAN haben Sie die M&amp;ouml;glichkeit, sich aktiv einzubringen und tragen damit ma&amp;szlig;geblich zum gesamten Unternehmenserfolg bei. Das dynamische Umfeld und die Internationalit&amp;auml;t unseres Gesch&amp;auml;fts bieten Ihnen zahlreiche Optionen, sich weiterzuentwickeln. Selbstverst&amp;auml;ndlich honorieren wir Ihren Einsatz und Sachverstand mit einem attraktiven Gehaltspaket. HARMAN ist ein Arbeitgeber, der Wert auf gleiche Besch&amp;auml;ftigungschancen legt.&lt;/p&gt;', 'Hildesheim, Deutschland', 'http://careers.stackoverflow.com/jobs/94772/projektleiter-automotive-software-m-w-harman-becker-automotive', 'Mon, 05 Oct 2015 10:02:02 Z', '2015-10-05T10:02:02Z', 1380);
