INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1701, 'Manager, Software Engineering  Search Intelligence at Ask (Oakland, CA)', '&lt;p&gt;&lt;strong&gt;Job Description&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;Ask.com is seeking qualified candidates for the Software Engineering Manager role to work with the Search Intelligence team at our Oakland, CA location. The Search Intelligence team has developed core technologies such as Related Search, Search Suggestions, Query Classification, Question Generation etc. The Manager will lead the development team to develop our next generation search technologies. It&amp;rsquo;s an exciting and challenging mission with the issues such as learning the user intention, understanding the web data, question generation, analyzing user logs etc. You will be working with a highly talented team of engineers using latest technologies and frameworks such as Apache SOLR, Hadoop, HBase, Redis to solve various problems.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The Role&lt;br&gt;We are looking for a leader who can work with business stakeholders within all our Ask business units, and manage a diverse technology team to -&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;To develop high performance and scalable systems.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Refine and reform our Search products for better user experience and engagement.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Actively participate in exploring key opportunities for next generation search technologies, develop quick prototypes to demonstrate the product.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Create and manage team&amp;rsquo;s roadmap, execute project plans with multiple teams in support of business goals&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Oakland, CA', 'http://careers.stackoverflow.com/jobs/99486/manager-software-engineering-search-ask', 'Mon, 05 Oct 2015 10:02:02 Z', '2015-10-05T10:02:02Z', 1701);
