INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1706, 'Frontend Javascript Developer at Ask (Oakland, CA)', '&lt;p&gt;&lt;strong&gt;Job Description&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br&gt;The role &amp;amp; your impact&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Passion, knowledge and experience within the JavaScript and Node.js ecosystem. You know how to navigate the vast and quickly evolving collection of open source projects and select the right tool for the job. You are the expert, as a front end developer, server side, or both!&lt;br&gt; Design and develop awesome experiences in our Node.js front-end platform that will delight our users.&lt;br&gt; Build out a new portfolio of products on a modern stack to support the growth of and expansion into the content space and beyond.&lt;br&gt;Focus on creating innovative and feature-rich content experiences and the best user experience.&lt;br&gt; Be a new member of a recently formed team. Get in early, collaborate, and contribute to our evolving Node.js front-end platform. We are actively recruiting a number of skilled developers who love working with JavaScript and are excited about working on something new! No legacy code here, just brand new projects to sink your teeth into!&lt;/p&gt;', 'Oakland, CA', 'http://careers.stackoverflow.com/jobs/99484/frontend-javascript-developer-ask', 'Mon, 05 Oct 2015 09:02:02 Z', '2015-10-05T09:02:02Z', 1701);
