INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1710, 'Senior C++ Developer: Rigging and Animation at Animal Logic (Moore Park, Australia)', '&lt;p&gt;Animal Logic is seeking a Senior Software Developer to join our core R&amp;amp;D group to help continue the evolution of its production tools and processes, especially in the area of character animation and rigging. The role will involve supporting development of improved tools and techniques in collaboration with our artistic staff.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will be working alongside some of the best artists and developers in the world applying your technical skills to create new tools and techniques for generating fluent character performances.&lt;/p&gt;', 'Moore Park, Australia', 'http://careers.stackoverflow.com/jobs/94768/senior-c-plus-plus-developer-rigging-and-animation-animal-logic', 'Mon, 05 Oct 2015 09:02:02 Z', '2015-10-05T09:02:02Z', 12);
