INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1718, 'Software Engineer - Virtual Reality at Futuremark (Espoo, Finland)', '&lt;p&gt;&lt;span&gt;Are you bored coding corporate apps and mindless mobile games? Wouldn''t you rather be working with the technologies that will shape the future of entertainment?&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;We''re looking for a talented developer to join our growing VR team in a full-time, permanent position. You''ll help develop VRMark, a &lt;/span&gt;&lt;a href="http://www.futuremark.com/benchmarks/vrmark" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;&lt;span&gt;VR benchmark &lt;/span&gt;&lt;/a&gt;&lt;span&gt;that uses a combination of software and hardware to measure VR system performance, latency, and accuracy, the key ingredients for convincing and comfortable virtual reality experiences.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;There are already more than a dozen VR headsets at various stages of development. But delivering a great VR experience relies on overcoming significant performance challenges. Working with the leading companies in the field, you''ll create VR benchmark tests for a range of content types from photos and video to games and simulations.&lt;/span&gt;&lt;/p&gt;', 'Espoo, Finland', 'http://careers.stackoverflow.com/jobs/99482/software-engineer-virtual-reality-futuremark', 'Mon, 05 Oct 2015 08:02:02 Z', '2015-10-05T08:02:02Z', 1718);
