INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1722, 'Front End Engineer (m/f) at Gymondo GmbH (Berlin, Deutschland)', '&lt;p&gt;Gymondo is looking for a &lt;strong&gt;Front End Engineer (m/f)&lt;/strong&gt; to join our development team and help us setting the trends in online Fitness and Nutrition world.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;We are in the process of migrating our frontend to a full blown AngularJS app that offers a more flexible workout and nutrition approach to our customers and partners hugely increasing the usability of our product. For this task we are searching for a Front End Engineer (m/f) with bulletproof experience and passion for front end tech like HTML, CSS, and for JavaScript tools frameworks like Angular JS, Backbone and Underscore.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Responsibilities:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work closely together with design and product teams to plan and implement breathtaking visually appealing features and products&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Wear the design hat and come up with design and usability improvements on your own&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Participate in maintaining and evolving the architecture of our main web app&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Evaluate upcoming technologies and trends for the web and use them for Gymondo&amp;rsquo;s webapp&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Berlin, Deutschland', 'http://careers.stackoverflow.com/jobs/99481/front-end-engineer-m-f-gymondo-gmbh', 'Mon, 05 Oct 2015 08:02:02 Z', '2015-10-05T08:02:02Z', 1722);
