INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1729, 'Cloud Entwickler (m/w) at ipt Innovation Process Technology (Zrich, Schweiz)', '&lt;p&gt;ipt ist ein Schweizer IT-Beratungsunternehmen mit &amp;uuml;ber &lt;a title="Mitarbeiter" href="http://ipt.ch/person/"&gt;100 Mitarbeitenden&lt;/a&gt;. &lt;a href="http://ipt.ch/partner/" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;Leading-edge Technologien&lt;/a&gt; sind genauso unser Credo wie eine motivierende Unternehmenskultur. Gemeinsam vor Ort mit unseren &lt;a href="http://ipt.ch/kunde/" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;Kunden&lt;/a&gt; realisieren wir innovative Software-L&amp;ouml;sungen f&amp;uuml;r komplexe gesch&amp;auml;ftliche Herausforderungen.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Wir entwickeln Individualsoftware mit &amp;laquo;asset-based Software Solutions&amp;raquo; unter Einsatz von f&amp;uuml;hrenden Softwareprodukten &amp;ndash; mit so wenig Code wie m&amp;ouml;glich. Unsere vielf&amp;auml;ltigen &lt;a href="http://ipt.ch/thema/" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;Themen&lt;/a&gt; sind SOA, ESB, Java EE, BPM, Middleware, Security, Testing, API-Management, DevOps und Cloud Integration.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Was Sie bei uns tun&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Sie entwickeln in einem kleinen, schlagkr&amp;auml;ftigen Projekt-Team innovative L&amp;ouml;sungen f&amp;uuml;r unsere Kunden. Sie beraten und begleiten den Kunden durch den ganzen SW-Entwicklungszyklus, vom Requirements Engineering &amp;uuml;ber die Realisierung bis zum Testing und Go-Live. Dank Modellierung und Konfiguration auf modernsten Cloud-Technologien wie ServicNow PaaS oder Dell Boomi iPaaS setzen Sie Business Requirements agil und effizient um. Sie nutzen vorhandene APIs und realisieren neue APIs z.B. f&amp;uuml;r mobile Apps.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Arbeitsort&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Unsere Kunden sind grosse Unternehmen mit eigener IT-Abteilung in der Deutschschweiz. Deren Standorte sind schwerpunktm&amp;auml;ssig im Raum Bern, Basel, Luzern und Z&amp;uuml;rich. Deshalb ist betreffend Arbeitsplatz Flexibilit&amp;auml;t gefordert und die Bereitschaft zu pendeln. Daf&amp;uuml;r fahren Sie 1. Klasse und k&amp;ouml;nnen auch im Zug arbeiten, solange das Projekt dies erm&amp;ouml;glicht.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Einstieg&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Der Start bei ipt l&amp;auml;uft individuell ab. Gemeinsam definieren wir Themen und Technologien, Sie arbeiten sich selbst&amp;auml;ndig ein. Selbstverst&amp;auml;ndlich unterst&amp;uuml;tzt Sie das ipt-Team und beantwortet gerne Ihre Fragen.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Weiterbildung&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;laquo;We hire for attitude and we train for skills&amp;raquo; ist ein Kernprinzip von ipt. Wir investieren viel in Sie. Deshalb sind Aus- und Weiterbildung f&amp;uuml;r uns sehr wichtig. Wer welche Zertifizierung macht respektive welche Konferenz-Teilnahme sinnvoll ist, wird auf individueller Basis besprochen, ebenso die finanzielle und zeitliche Unterst&amp;uuml;tzung&lt;/p&gt;', 'Zrich, Schweiz', 'http://careers.stackoverflow.com/jobs/84668/cloud-entwickler-m-w-ipt-innovation-process', 'Mon, 05 Oct 2015 08:02:02 Z', '2015-10-05T08:02:02Z', 1729);
