INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1732, 'Senior Software Developer Visual Basic .NET / C# at Strategy Compass GmbH (Dsseldorf, Deutschland)', '&lt;p&gt;Zur Verst&amp;auml;rkung unseres Entwickler-Teams f&amp;uuml;r die Software &lt;a title="QuickSlide f&amp;uuml;r PowerPoint" href="http://www.quickslide-powerpoint.com"&gt;QuickSlide f&amp;uuml;r PowerPoint&lt;/a&gt; suchen wir eine/n Software-Entwickler/in mit umfangreicher Erfahrung in Visual Basic .NET oder C#. QuickSlide wird von zahlreichen f&amp;uuml;hrenden Unternehmen eingesetzt, um die Qualit&amp;auml;t ihrer Pr&amp;auml;sentationen zu steigern, effizienter zu arbeiten und Pr&amp;auml;sentationen zentral zu verwalten.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Warum wir?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Bei uns erwarten Sie&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;ein schnell wachsendes Unternehmen mit spannenden Kunden&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;sehr erfahrene Kollegen und Programmieren auf hohem Niveau&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;gro&amp;szlig;e N&amp;auml;he zum Endprodukt, f&amp;uuml;r das Sie entwickeln&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;ein nettes, ambitioniertes, wertsch&amp;auml;tzendes Team und der sch&amp;ouml;ne Standort D&amp;uuml;sseldorf&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;flexible Arbeitszeiten und die M&amp;ouml;glichkeit, teilweise im Home Office zu arbeiten&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Warum Sie?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Sie passen zu uns, weil Sie&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;einschl&amp;auml;gige Berufserfahrung mitbringen und sich bestens in Visual Basic .NET oder C# auskennen&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;auch mit komplexeren Code-Strukturen umgehen k&amp;ouml;nnen und gerne selbst vor- und weiterdenken&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;mit einer Mischung aus gro&amp;szlig;en Weiterentwicklungen, kleinen Projekten dazwischen und gelegentlichen Kundentelefonaten gut klar kommen&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;nett und kollegial sind und in ihrem Bereich erstklassig sein wollen&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;Nicht Pflicht, aber von Vorteil sind au&amp;szlig;erdem vertiefte Kenntnisse der Visual Studio Tools for Office und des Objekt-Modells einer MS Office-Anwendung, sowie von SharePoint und XML. Da wir f&amp;uuml;r gro&amp;szlig;e und internationale Kunden arbeiten, sind gute Englisch-Kenntnisse wichtig.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Passt?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Wenn Sie das Gef&amp;uuml;hl haben, Sie sind bei uns richtig, freuen wir uns sehr auf Ihre vollst&amp;auml;ndigen Bewerbungsunterlagen inklusive Gehaltsvorstellung!&lt;/p&gt;', 'Dsseldorf, Deutschland', 'http://careers.stackoverflow.com/jobs/99242/senior-software-developer-visual-basic-net-c-sharp-strategy-compass-gmbh', 'Mon, 05 Oct 2015 07:02:02 Z', '2015-10-05T07:02:02Z', 1732);
