INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1748, 'Security-minded multi-talented developer to work in best location on earth at CryptoPhoto (Peregian Beach, Australia)', '&lt;p&gt;You know how sometimes, people ask "what do you do?". &amp;nbsp;If&amp;nbsp;work with&amp;nbsp;us, the next time they ask, you can say: "You know that cool way you log in to your bank, which keeps you safe? &amp;nbsp;I build that". &amp;nbsp;Your work will literally be in the pockets of almost everyone online, in the months to come.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Our LAMP backend supports mobile (phone, tablet, watch, token) clients, and our SDK/Plugins hook existing web tech to make authentication operations immune to phishing, and to neutralize malware.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Oh yeah, and that question:&amp;nbsp;"what do you do?" -&amp;nbsp;the person asking that? &amp;nbsp;They won''t be next to you on the train;&amp;nbsp;they''ll be alongside you in the &lt;a href="https://www.google.com.au/search?q=noosa+surf&amp;amp;es_sm=93&amp;amp;source=lnms&amp;amp;tbm=isch" rel="nofollow" rel="nofollow" rel="nofollow"&gt;surf&lt;/a&gt; awaiting the next break, or &lt;a href="https://www.google.com.au/search?q=noosa+fishing&amp;amp;espv=2&amp;amp;biw=1582&amp;amp;bih=1319&amp;amp;source=lnms&amp;amp;tbm=isch" rel="nofollow" rel="nofollow" rel="nofollow"&gt;fishing &lt;/a&gt;(no ph) in the boat nearby, or climbing back up the dune for the next &lt;a href="https://www.google.com.au/search?q=fraser+island+sand+tobogganing&amp;amp;espv=2&amp;amp;biw=1582&amp;amp;bih=1319&amp;amp;source=lnms&amp;amp;tbm=isch" rel="nofollow" rel="nofollow" rel="nofollow"&gt;sandboard&lt;/a&gt; run, or drifting down &lt;a href="https://www.google.com.au/search?q=Eli+Creek&amp;amp;es_sm=93&amp;amp;source=lnms&amp;amp;tbm=isch" rel="nofollow" rel="nofollow" rel="nofollow"&gt;Eli Creek&lt;/a&gt;, or maybe just next to you in your &lt;a href="https://www.google.com.au/search?q=fraser+island+4x4&amp;amp;espv=2&amp;amp;biw=1582&amp;amp;bih=1319&amp;amp;source=lnms&amp;amp;tbm=isch" rel="nofollow" rel="nofollow" rel="nofollow"&gt;car&lt;/a&gt;.&lt;/p&gt;', 'Peregian Beach, Australia', 'http://careers.stackoverflow.com/jobs/99475/security-minded-multi-talented-developer-to-work-cryptophoto', 'Mon, 05 Oct 2015 04:02:02 Z', '2015-10-05T04:02:02Z', 1748);
