INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1750, 'Web Developer - Full Stack at INFODYNAMICS, Inc. (Denver, CO)', '&lt;p&gt;INFODYNAMICS, Inc has a DYNAMIC position for the right web developer&amp;hellip;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;With 40% growth this year, our customers are asking to get even more out of our productivity software.&amp;nbsp; We are looking for developers in Indianapolis, IN and Denver, CO.&amp;nbsp; We are expanding our presence in Denver and both locations work collaboratively with each other&amp;nbsp;to expand&amp;nbsp;our best-in-class productivity packages INTACT, INPOINT and Books SMART.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are seeking a highly motivated, successful candidate with excellent problem solving and technical skills who is eager to help move our product forward. The successful candidate will work&amp;nbsp;from our&amp;nbsp;Denver location&amp;nbsp;and will need to be able to not only learn our existing software products and technology stack inside and out, but also embrace&amp;nbsp;additional technologies for our new product platform.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;strong&gt;:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work with the software team to design and develop&amp;nbsp;a full-featured&amp;nbsp;web application and related services using modern web technologies (e.g.HTML, CSS, JavaScript, Ajax, jQuery, Django/Python)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Build intuitive, rich and functional user interfaces using dynamic web presentations technology (familiarity with various JavaScript/HTML/CSS UI platforms such as&amp;nbsp;Bootstrap, jQuery UI, etc. is preferred)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Assist in&amp;nbsp;the design and deployment of web servers&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Denver, CO', 'http://careers.stackoverflow.com/jobs/97168/web-developer-full-stack-infodynamics-inc', 'Mon, 05 Oct 2015 00:02:02 Z', '2015-10-05T00:02:02Z', 1750);
