INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1761, 'Director of Engineering at Originate (New York, NY)', '&lt;p&gt;Originate is looking for a Head of Engineering to help build, guide and grow the best software team in New York!&amp;nbsp; We invest and then build innovative web/mobile products in partnership with entrepreneurs and enterprises and we need a well-rounded software expert to be in charge of the team in the New York office.&amp;nbsp;&lt;/p&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/77820/director-of-engineering-originate', 'Sun, 04 Oct 2015 23:02:02 Z', '2015-10-04T23:02:02Z', 1761);
