INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1768, 'Senior Enterprise Full-Stack Web Developer at Catapult Consulting LLC (San Francisco, CA) (allows remote)', '&lt;p&gt;&lt;strong&gt;Calling full-stack rock stars!&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Join a cutting edge technology team working on a CMS in a very desirable work environment. Focus is primarily on new feature implementation. Position is long-term and you''ll have the option to telecommute and work remotely.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Skills&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;OS X, JavaScript, Ember.js, Backbone, AngularJS, Ember Data, Java, Solr, Cassandra, RESTful APIs, HTML, CSS, Bootstrap, JSON, Enterprise Level Software Engineering&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Key Qualifications&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Enterprise-level commercial software engineer (12+ years) with a strong focus on full-stack web solutions&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Significant JavaScript expertise: specifically Ember.js, Backbone or AngularJS and Ember Data&lt;/li&gt;&lt;br /&gt;&lt;li&gt;High level of proficiency with Java, Solr, Cassandra, RESTful APIs and JSON&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Proficient with HTML/CSS/Bootstrap&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Desirable Skills&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Technical lead&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Investigation capability&lt;/li&gt;&lt;br /&gt;&lt;li&gt;OS X, Linux, Unix&lt;/li&gt;&lt;br /&gt;&lt;li&gt;IntelliJ, Java 8, Git/GitHub, Gradle&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience writing unit, integration and smoke tests&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Familiarity with Jenkins, Greenhouse, Phantom JS, JSHint, Selenium Debugging and reporting with Splunk&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Server-side monitoring with Nagios, Graphite, AppDynamics&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Agile environment&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience working with CMSs&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Education&lt;/strong&gt;: BS in Computer Science or equivalent work experience.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;To be considered, please forward your resume (including current address, phone number and email address) as well as current work status. No third parties please. Candidates must either have US citizenship or a green card.&lt;/p&gt;', 'San Francisco, CA', 'http://careers.stackoverflow.com/jobs/99473/senior-enterprise-full-stack-web-developer-catapult-consulting-llc', 'Sun, 04 Oct 2015 22:02:02 Z', '2015-10-04T22:02:02Z', 1768);
